function selectPartSpace(spaceName) {
    if (!spaceName) return;
    this.selectedPartSpace = spaceName;
    this.get('Parts').setList(
        this.getParts(spaceName)
            .sortBy(function(ea) {
                return this.getPartVersions(spaceName, ea).length
            }, this)
            .reverse()
            .collect(function(ea) {
                return {
                    string:  this.getPartVersions(spaceName, ea).length + " " + ea,
                    value: ea,
                    isListItem: true
                }
            }, this)
    )
    var totalVersions = this.getParts(spaceName).inject(0, function(sum, ea) {
        return sum + this.getPartVersions(spaceName, ea).length
    }, this)

    this.get("Parts").setSelection(undefined)
    this.get("Details").textString = "total versions: " + totalVersions

}

