function printPartDetails(partSpaceName, name) {
    var s ="## " + name + "\n"
    var versions = this.getPartVersions(partSpaceName, name)
    s += "Versions:" + versions.length + "\n"
    s += "### Development per month" + "\n"
    s += this.histogram(versions,
        function(ea) { return ea.date.format("yyyy-mm")})
        .sortBy(function(ea) { return ea.key})
        .join("\n") + "\n"

    s += "### Authors" + "\n"
    s += this.histogram(versions,
        function(ea) { return ea.author})
        .join("\n") + "\n"

    return s
}

651.source:
function histogram(collection, keyFunc) {
    var h = {}
    collection.each(function(ea) {
        var key = keyFunc(ea)
        h[key] = h[key] || 0;
        h[key] ++  
    })
    return Properties.own(h)
        .collect(function(ea) {
            return {
                key: ea, 
                value: h[ea], 
                toString: function() {
                    return this.key + ": " + this.value
                } 
            }
        })
        .sortBy(function(ea) { return ea.value})
        .reverse()
}

