function getParts(partSpace) {
    if (!this.cachedParts) this.cachedParts = {}
    if (!this.cachedParts[partSpace]) {
        var ea = new WebResource(URL.root.withFilename("PartsBin/")
            .withFilename(partSpace))
        ea.getSubElements(1)
        var parts =  ea.subDocuments.invoke('getURL')
            .select(function(ea) {
                return ea.filename().endsWith(".json")
            })
            .collect(function(ea) {
                return ea.filename().replace(".json","")
            }).sort()
        this.cachedParts[partSpace] = parts
    }
    return this.cachedParts[partSpace]
}

