function getPartVersions(partSpace, part) {
    if(!this.partsVersions)
        this.partsVersions = {}
    var key = partSpace + "$"+ part
    if (!this.partsVersions[key]) {
        var url = URL.root.withFilename("PartsBin/" + partSpace + "/" + part +".json")
        var wr = new WebResource(url)
        this.partsVersions[key] = wr.getVersions().versions
    }
    return this.partsVersions[key]
}

