function loadAllVersions() {
    var partSpaces = this.getPartSpaces()
    // this is slow
    var totalVersions = 0
    this.get('Summary').textString = "loading: "
    partSpaces.doAndContinue(
        function(next, ea) {
            this.selectPartSpace(ea)
            var parts = this.getParts(ea)
            parts.each(function(eaPart) {
                var versions = this.getPartVersions(ea, eaPart)
                totalVersions += versions.length
            }, this)
            this.get('Summary').textString += (ea + ", ")
            next.delay(0.1)
        },
        function() {
            this.totalVersions = totalVersions
            this.updateSummary()
        }.bind(this),
        this)
}

