function updateSummary() {

    var partSpaceNames = this.getPartSpaces();
    var totalParts = partSpaceNames.inject(0, function(sum, ea) {
        return sum + this.getParts(ea).length
    }, this)

    this.get("Summary").textString =
        "PartSpaces: " + partSpaceNames.length + "\n"
        + "Total Parts: " + totalParts + "\n"
        + "Total Versions: " + this.totalVersions + "\n"

}

