function shorten() {
    var originalUrlString = 'http://www.lively-kernel.org/' + 
        this.get('urlInput').getTextString().trim();
    var shortenedUrlString = 'http://www.lively-kernel.org/goto/' +
        this.get('gotoInput').getTextString().trim();

    var gotoResource = new WebResource(new URL(shortenedUrlString));

    var shortenUrl = function (confirmed) {
        if (!confirmed) return;
        var putUrl = shortenedUrlString + '?url=' + escape(originalUrlString);
        var webR = new WebResource(new URL(putUrl));
        var response = webR.put();
        if (response.status.isSuccess()) {
            alertOK('Shortened ' + originalUrlString + ' to ' + 
                shortenedUrlString);
        } else {
            alert('Not shortened: Response status = ' + 
                response.status.code());
        }
    }

    // versions of chromium don't handle 302 responses correctly
    // will overwrite there...
    if (!gotoResource.exists()) {
        shortenUrl(true);
    } else {
        var message = 'Overwrite ' + shortenedUrlString + ' ?';
        var dialog = new lively.morphic.ConfirmDialog(
            message, shortenUrl);
        this.world().openDialog(dialog);
    }
}
