function showActivities() {
    // clear table
    // this.get('VersionTable').textString = ""
    this.get('Status').textString = "loading"

    // this is what I call a real script: no refactoring at all :-)
    var url = this.get("URL").textString

    module('server.nodejs.WebInterface').load(true);
    var server = new CommandLineServerInterface()

    var limit = Number(this.get('Limit').textString) || 100

    var cmd = 'svn log -v --xml --limit ' +limit + " "+ url;
    connect(server, 'result', this, 'parseLog')
    server.runCommand(cmd)

}

