function displayFilteredText() {
    if (!this.logEntries) return;
    var table = [];
        this.logEntries.each(function(ea) {
            ea.paths.each(function(eaPath){ 
                table.push([
                    ea.date ? ea.date.format("yyyy-mm-dd HH:MM") : "",
                    ea.revision,
                    ea.author,
                    ""+ eaPath.path])
            })
        })
    var filter = this.getFilterRegExp();
    var filteredTable = table.select(function(row) {
        var s = row.join(" ");
        return s.match(filter)
    })
    this.get('Table').textString = this.printSparseTable(filteredTable, [16, 6, 16, 200])
    this.get('Table').applyStyle({textColor: Color.darkGray})
}

