function parseLog(response) {

    this.xml = response.stdout; // cache for development
    var xmlDoc = $.parseXML(this.xml)

    module('lib.DateJS').load(true)
    var parseDate = function(s) {
        try {
            var m = s.match(/(\d\d\d\d-\d\d-\d\d)T(\d\d:\d\d:\d\d)/)
            return Date.parse(m[1] + " " + m[2])
        } catch(e) {
            return undefined
        }
    }
    var parse = function(obj,node, tag) {
        var found = $(node).find(tag)[0];
        if (found) obj[tag] = Strings.removeSurroundingWhitespaces(found.textContent);
    }
    this.logEntries = $A($(xmlDoc).find('logentry')).collect(function(ea) {
        var obj = {
            revision: ea.getAttribute('revision'),
            paths: $A($(ea).find('path')).collect(function(eaPath) {
                return {
                    action: eaPath.getAttribute('action'),
                    kind: eaPath.getAttribute('kind'),
                    path: Strings.removeSurroundingWhitespaces(eaPath.textContent)}
                })};
        parse(obj, ea, "author")
        parse(obj, ea, "msg")
        parse(obj, ea, "date")
        obj.date = parseDate(obj.date);
        return obj
    })
    this.displayFilteredText();
}

