function printSparseTable(table, limits) {
    // Example: 
    // this.printSparseTable([["a", "b", "c"],["a", "b2", "c2"]], [3,3,3])
    // ->
    // a  b   c 
    //    b2  c2 

    var current = [];
    limits = limits || [];
    var forceStringWidth = function(s, n, left) {
        return Strings.pad(s.substring(0,n), n - s.length, left)
    }
    return table.collect(function(eaRow) {
        return eaRow.collect(function(eaColumn, index) {
            var n = limits[index] || 16;
            if (eaColumn == current[index]) {
                return forceStringWidth("", n)
            } else {
                current[index] = eaColumn ;
                return forceStringWidth(eaColumn, n);
            }
        }).join(' ')
    }).join('\n')
}

